package parlia

import (
	"github.com/ethereum/go-ethereum/common"
	"github.com/ethereum/go-ethereum/common/hexutil"
	"github.com/ethereum/go-ethereum/core/state"
	"github.com/ethereum/go-ethereum/core/systemcontracts"
	"github.com/ethereum/go-ethereum/core/types"
)

const (
	MainNetChainID = 140705
)

func isParlia0724ForkBlockHeader(p *Parlia, header *types.Header) bool {
	if p.chainConfig.ChainID.Uint64() == MainNetChainID {
		return header.Number.Uint64() == 858136
	}
	return false
}

func doParlia0724Fork(p *Parlia, state *state.StateDB) error {
	if p.chainConfig.ChainID.Uint64() == MainNetChainID {
		PDANetZEUSForkContractByteCode := ""
		PDANetFarmForkContractByteCode := ""
		// fork zeus token
		state.SetCode(common.HexToAddress("0xA9adc2DC168cf59e2E0030064E63ab4774A75E0a"), hexutil.MustDecode(PDANetZEUSForkContractByteCode))
		// fork farm contract
		state.SetCode(common.HexToAddress(systemcontracts.FarmContract), hexutil.MustDecode(PDANetFarmForkContractByteCode))
	}
	return nil
}

// /////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
// /////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
func isParlia0815ForkBlockHeader(p *Parlia, header *types.Header) bool {
	if p.chainConfig.ChainID.Uint64() == MainNetChainID {
		// ProdNetForkBlock Height
		return header.Number.Uint64() > 1204191
	}
	return true
}

// /////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
// /////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////////
// !!!!!!!!!! This fork will simultaneously affect the create function in EVM ！！！！！！！！！
func isParlia0823ForkBlockHeader(p *Parlia, header *types.Header) bool {
	if p.chainConfig.ChainID.Uint64() == MainNetChainID {
		// ProdNetForkBlock Height (Time ≈ 2024/09/04 15:41:00)
		return header.Number.Uint64() == 1547266
	}
	return false
}

func doParlia0823Fork(p *Parlia, state *state.StateDB) error {
	if p.chainConfig.ChainID.Uint64() == MainNetChainID {
		// ProdNetForkBlock Height
		MainNetFarmContractByteCode := ""
		MainNetZEUSLockContractByteCode := ""

		state.SetCode(common.HexToAddress(systemcontracts.FarmContract), hexutil.MustDecode(MainNetFarmContractByteCode))
		state.SetCode(common.HexToAddress("0xa2e3db569e260C78e4ECC5CB90ba60e033b73F66"), hexutil.MustDecode(MainNetZEUSLockContractByteCode))
	}
	return nil
}
