package systemcontracts

import (
	"encoding/hex"
	"fmt"
	"math/big"
	"strings"

	"github.com/ethereum/go-ethereum/common"
	"github.com/ethereum/go-ethereum/core/state"
	"github.com/ethereum/go-ethereum/log"
	"github.com/ethereum/go-ethereum/params"
)

type UpgradeConfig struct {
	BeforeUpgrade upgradeHook
	AfterUpgrade  upgradeHook
	ContractAddr  common.Address
	CommitUrl     string
	Code          string
}

type Upgrade struct {
	UpgradeName string
	Configs     []*UpgradeConfig
}

type upgradeHook func(blockNumber *big.Int, contractAddr common.Address, statedb *state.StateDB) error

const (
	mainNet = "Mainnet"
	testnet = "Testnet"
)

var (
	GenesisHash common.Hash

	validatorDeployUpgrade = make(map[string]*Upgrade)

	validatorActivityUpgrade = make(map[string]*Upgrade)

	usdtOverrideUpgrade = make(map[string]*Upgrade)
)

func init() {
	validatorDeployUpgrade[mainNet] = &Upgrade{
		UpgradeName: "validatorUpgrade",
		Configs: []*UpgradeConfig{
			{
				ContractAddr: common.HexToAddress(ValidatorContract),
				CommitUrl:    "",
				Code:         "",
			},
			{
				ContractAddr: common.HexToAddress(AddressTreeContract),
				CommitUrl:    "",
				Code:         "",
			},
			{
				ContractAddr: common.HexToAddress(SystemDaoContract),
				CommitUrl:    "",
				Code:         "",
			},
		},
	}

	validatorDeployUpgrade[testnet] = &Upgrade{
		UpgradeName: "validatorUpgrade",
		Configs: []*UpgradeConfig{
			{ContractAddr: common.HexToAddress(ValidatorContract),
				CommitUrl: "",
				Code:      "",
			},
		},
	}

	
	validatorActivityUpgrade[mainNet] = &Upgrade{
		UpgradeName: "validatorUpgrade",
		Configs: []*UpgradeConfig{
			{
				ContractAddr: common.HexToAddress(SystemDaoContract),
				CommitUrl:    "",
				Code:         "",
			},
			{
				ContractAddr: common.HexToAddress(FarmContract),
				CommitUrl:    "",
				Code:         "",
			},
			{
				ContractAddr: common.HexToAddress(AddressTreeContract),
				CommitUrl:    "",
				Code:         "",
			},
		},
	}

	
	usdtOverrideUpgrade[mainNet] = &Upgrade{
		UpgradeName: "usdtOverrideUpgrade",
		Configs: []*UpgradeConfig{
			{
				ContractAddr: common.HexToAddress("0x31813F817351c0898B2A06687517385c16615016"),
				CommitUrl:    "",
				Code:         "",
			},
		},
	}
}

func UpgradeBuildInSystemContract(config *params.ChainConfig, blockNumber *big.Int, statedb *state.StateDB) {
	if config == nil || blockNumber == nil || statedb == nil {
		return
	}
	var network string
	switch GenesisHash {
	case params.PDANetGenesisHash:
		network = mainNet
	case params.TestnetGenesisHash:
		network = testnet
	default:
		network = mainNet
	}

	if config.IsOnValidatorDeployForkBlock(blockNumber) {
		logger := log.New("system-contract-upgrade \"validator deploy fork\"", network)
		applySystemContractUpgrade(validatorDeployUpgrade[network], blockNumber, statedb, logger)
	}

	if config.IsOnValidatorActivityForkBlock(blockNumber) {
		logger := log.New("system-contract-upgrade \"validator activity fork\"", network)
		applySystemContractUpgrade(validatorActivityUpgrade[network], blockNumber, statedb, logger)
	}

	if blockNumber.Cmp(big.NewInt(3226800)) == 0 && network == mainNet {
		logger := log.New("system-contract-upgrade \"usdt overrider fork\"", network)
		applySystemContractUpgrade(usdtOverrideUpgrade[network], blockNumber, statedb, logger)
	}
}

func applySystemContractUpgrade(upgrade *Upgrade, blockNumber *big.Int, statedb *state.StateDB, logger log.Logger) {
	if upgrade == nil {
		logger.Info("Empty upgrade config", "height", blockNumber.String())
		return
	}

	logger.Info(fmt.Sprintf("Apply upgrade %s at height %d", upgrade.UpgradeName, blockNumber.Int64()))
	for _, cfg := range upgrade.Configs {
		logger.Info(fmt.Sprintf("Upgrade contract %s to commit %s", cfg.ContractAddr.String(), cfg.CommitUrl))

		if cfg.BeforeUpgrade != nil {
			err := cfg.BeforeUpgrade(blockNumber, cfg.ContractAddr, statedb)
			if err != nil {
				panic(fmt.Errorf("contract address: %s, execute beforeUpgrade error: %s", cfg.ContractAddr.String(), err.Error()))
			}
		}

		newContractCode, err := hex.DecodeString(strings.TrimPrefix(cfg.Code, "0x"))
		if err != nil {
			panic(fmt.Errorf("failed to decode new contract code: %s", err.Error()))
		}
		statedb.SetCode(cfg.ContractAddr, newContractCode)

		if cfg.AfterUpgrade != nil {
			err := cfg.AfterUpgrade(blockNumber, cfg.ContractAddr, statedb)
			if err != nil {
				panic(fmt.Errorf("contract address: %s, execute afterUpgrade error: %s", cfg.ContractAddr.String(), err.Error()))
			}
		}
	}
}
